-- Schema for Subjects Project

-- Table of subjects
CREATE TABLE subject (
                         id SERIAL PRIMARY KEY,
                         name VARCHAR(50) NOT NULL,
                         start_text VARCHAR(100),
                         duration_text VARCHAR(100),
                         duration_number NUMERIC(5,2)
);

-- Table for prices by tier
CREATE TABLE subject_prices (
                                subject_id         INT            NOT NULL REFERENCES subject(id),
                                tier               VARCHAR(10)    NOT NULL,
                                full_price         INT,
                                monthly_price      INT,
                                installment_price  INT,
                                PRIMARY KEY (subject_id, tier)
);

-- Table for descriptive card items by tier
CREATE TABLE subject_cards (
                               id SERIAL PRIMARY KEY,
                               subject_id INT NOT NULL REFERENCES subject(id),
                               tier VARCHAR(10) NOT NULL,
                               text_line TEXT
);

